mapper.update:
UPDATE tbl_notice

	nomoddate = sysdate

		, notitle = #{noTitle}


		, notext = #{noText}


		, nokeyword = #{noKeyword}



WHERE nonum = #{noNum}

UPDATE tbl_notice SET
noviewcnt = noviewcnt+1
WHERE nonum = #{noNum}

mapper.select:
SELECT b.* FROM (SELECT ROW_NUMBER() OVER (ORDER BY noregdate DESC) rnum
, nonum , notitle , notext , nokeyword , adminid , noregdate , noviewcnt FROM tbl_notice WHERE nonum > 0
) b WHERE (rnum >= #{pageStart} AND rnum <= #{pageEnd})

SELECT uri
FROM tbl_link
WHERE filename=#{fullName}
