mapper.insert:
INSERT INTO TBL_APPLY ( appnum
                      , sdate
                      , edate
                      , dayinfo
                      , spec
                      , price
                      , link
                      , cournum
                      , stuid )
VALUES ( seq_apply.nextval
       , #{sdate}
       , #{edate}
       , #{dayinfo}
       , #{spec}
       , #{price}
       , #{link}
       , #{cournum}
       , #{stuid} )

mapper.update:
		UPDATE TBL_APPLY a


	   	    	 a.sdate   = #{sdate}


      	       , a.edate   = #{edate}


      	       , a.dayinfo   = #{dayinfo}


	     	   , a.spec    = #{spec}


	    	   , a.price   = #{price}


	           , a.link    = #{link}


	   		   , a.cournum = #{cournum}


	   		   , a.stuid = #{stuid}


	 	    	WHERE a.appnum  = #{appnum}
