mapper.insert:
INSERT INTO tbl_company (
  compnum
, compname
, zipcode
, phone
, frontaddr
, rearaddr
, compfield
, imglink
, compdesc
, target 
, division)
VALUES ( seq_company.nextval
		, #{compname}
		, #{zipcode}
		, #{phone}
		,#{frontaddr}
		, #{rearaddr}
		, #{compfield}
		, #{imglink}
		, #{compdesc}
		, #{target}
		, #{division})

mapper.update:
UPDATE tbl_company
SET 

	 compname = #{compname}


	, zipcode = #{zipcode}


	, phone = #{phone}


	, frontaddr = #{frontaddr}


	, rearaddr = #{rearaddr}


	, compfield = #{compfield}


	, imglink = #{imglink}


	, compdesc = #{compdesc}


	, target = #{target}

	, division = #{division}

WHERE compnum = #{compnum}

mapper.select:
SELECT compnum , compname , zipcode , phone , frontaddr , rearaddr , compfield , imglink , compdesc , target , division FROM tbl_company WHERE compnum > 0
AND division = #{division}
ORDER BY compnum DESC

if.if:
