mapper.update:
update tbl_video_board
set
title = #{title}
, link = #{link}
, content = #{content}
, moddate = sysdate
, compnum = #{compnum}
where num = #{num}

UPDATE tbl_video_board b
SET b.viewcnt = b.viewcnt + 1
WHERE b.num = #{num}

mapper.select:
SELECT b.* FROM (SELECT
, v.num , v.adminid , v.link , v.title , v.regdate , v.viewcnt , v.content , v.compnum , c.compname FROM tbl_video_board v, tbl_company c WHERE num > 0 and v.compnum = c.compnum
) b WHERE (rnum >= #{pageStart} AND rnum <= #{pageEnd})

SELECT b.* FROM (SELECT
, v.num , v.adminid , v.link , v.title , v.regdate , v.viewcnt , v.content , v.compnum , c.compname FROM tbl_video_board v, tbl_company c WHERE num > 0 and v.compnum = 1 and v.compnum = c.compnum
) b WHERE (rnum >= #{pageStart} AND rnum <= #{pageEnd})
