mapper.insert:
INSERT INTO tbl_employee (  empnum 
                          , empname
                          , empphone
                          , empemail
                          , position
                          , compnum

                          , snslink1


                          , snslink2


                          , snslink3


                          , filenum


                          , empdesc

                         )  
				  VALUES (  seq_employee.nextval
				          , #{empname} 
				          , #{empphone}
				          , #{empemail}
				          , #{position}
				          , #{compnum}

				          , #{snslink1}


				          , #{snslink2}


				          , #{snslink3}


				          , #{filenum}


				          , #{empdesc}

				  	   )

mapper.update:
		UPDATE tbl_employee 

			        empname  = #{empname}
                  , empphone = #{empphone}
                  , empemail = #{empemail}
                  , position = #{position}
                  , compnum  = #{compnum}

                  , snslink1 = #{snslink1}


                  , snslink2 = #{snslink2}


                  , snslink3 = #{snslink3}


                  , filenum  = #{filenum}


                  , empdesc  = #{empdesc}


		 WHERE empnum = #{empnum}

mapper.delete:
DELETE tbl_emp_file
 WHERE filenum = (SELECT filenum
                    FROM tbl_employee
                   WHERE empnum = #{empnum}
                  )

mapper.select:
SELECT name
  FROM tbl_emp_file
 WHERE filenum = (SELECT filenum
                    FROM tbl_employee
                   WHERE empnum = #{empnum}
                 )

if.if:
AND (   empname   like '%' || #{keyword} || '%'
     OR position   like '%' || #{keyword} || '%'
    )
