mapper.update:
 UPDATE tbl_program_board
	SET title = #{title}
	  , content = #{content}
	  , link = #{link}
	  , compnum = #{compnum}
	  , category = #{category}
	  , moddate = sysdate
  WHERE intronum = #{intronum}

mapper.select:
select intronum , title , link , content from tbl_program_board where
category = 1
category = ${category}
and compnum = 1
and compnum = ${compnums}
