mapper.insert:
INSERT INTO TBL_STUDENT ( stuid
                        , stupw
                        , stuname
                        , stuemail
                        , stuphone
                        , compnum
                        )
VALUES ( #{stuid}
       , #{stupw}
       , #{stuname}
       , #{stuemail}
       , #{stuphone}
       , #{compnum}
	)

mapper.update:
		UPDATE TBL_STUDENT s


	   	    	 s.stuname   = #{stuname}


	   	       , s.stupw   = #{stupw}


      	       , s.stuemail   = #{stuemail}


      	       , s.stuphone   = #{stuphone}


	 	    	WHERE s.stuid  = #{stuid}
