mapper.update:
UPDATE tbl_event_board e
SET e.title = #{title}
,
e.content = #{content}
, e.paylink = #{paylink}
, e.applylink =
#{applylink}
, e.moddate = sysdate
WHERE e.num = #{num}

mapper.delete:
DELETE FROM tbl_event_file f
WHERE f.num = #{num} and
f.type = ${type}

mapper.update:
UPDATE tbl_event_board e
SET e.viewcnt = e.viewcnt + 1
WHERE e.num = #{num}

mapper.select:
SELECT b.* FROM (SELECT
, num , adminid , title , content , regdate , viewcnt FROM tbl_event_board WHERE num > 0
) b WHERE (rnum >= #{pageStart} AND rnum <= #{pageEnd})

SELECT e.name
, e.type
FROM
tbl_event_file e
WHERE e.num = #{num}
AND e.type = #{type}
