mapper.update:
update tbl_img_board
set title = #{title}
, content = #{content}
, intronum = #{intronum}
, adminid = #{adminId}
, moddate = sysdate
where imgnum = #{imgnum}

update tbl_img_board
set viewcnt = viewcnt + 1
where imgnum = #{imgnum}

mapper.select:
SELECT b.* FROM (select
, img.imgnum , img.intronum , img.title , img.content , img.adminid , img.regdate , img.viewcnt , (select f.name from tbl_img_file f where f.num = img.imgnum and rownum = 1) name FROM tbl_img_board img WHERE img.imgnum > 0
)b where (rnum >= #{pageStart} AND rnum <= #{pageEnd})
